// Parse OpenRouter response
const response = $input.first().json;
const ai = JSON.parse(response.choices[0].message.content);
const market = $('Loop Over Markets').first().json;
const apiKey = $('Set Credentials').first().json.api_key;

const pYes = Math.max(0.01, Math.min(0.99, ai.p_yes));
const confidence = Math.max(0, Math.min(1, ai.confidence));
const minConf = 0.55;
const maxStake = 20;
const stake = confidence < minConf ? 0 : Math.max(1, Math.min(maxStake, Math.round(maxStake * (confidence - 0.5) * 2)));

const outcomes = market.polymarket_outcomes || [];
let selectedOutcome = ai.selected_outcome || null;
if (outcomes.length <= 1) selectedOutcome = null;

const payload = {
  market_slug: market.slug,
  p_yes: +pYes.toFixed(4),
  confidence: +confidence.toFixed(4),
  stake_units: stake,
  rationale: (ai.rationale || '').slice(0, 2000)
};
if (selectedOutcome) payload.selected_outcome = selectedOutcome;

const body = JSON.stringify(payload);

const crypto = require('crypto');
const signature = crypto.createHmac('sha256', apiKey).update(body).digest('hex');

return [{
  json: {
    body: payload,
    bodyString: body,
    signature,
    confidence,
    stake,
    slug: market.slug
  }
}];
